import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Tabs } from "../../components/Tabs";
import { cartProducts } from "../../stores/cart/cartSlice";
import { fetchProducts, getAllProducts } from "../../stores/menu/productsSlice";

const Cart = () => {
    const dispatch = useDispatch();
    const cart = useSelector(cartProducts);
    console.log(cart);
    const [activeTab, setActiveTab] = useState('Summary');
    const [activeTabIndex, setActiveTabIndex] = useState(0);

    const [categories, setCategories] = useState(['Summary', 'Address', 'Payment']);

    const onTabSwitch = (newActiveTab) => {
        setActiveTab(newActiveTab);
        let index = categories.findIndex(category => newActiveTab === category);
        if (index > -1) {
            setActiveTabIndex(index);
        } else {
            setActiveTabIndex(0);
        }
    }
    if (!cart || cart.length === 0) {
        return (
            <div className="bg-white h-full text-black flex justify-center p-4">
                <h1>Your Cart is empty</h1>
            </div>
        )
    }
    return (
        <div className="bg-white h-screen text-black mx-auto mt-2 border border-gray-200 p-4 md:w-2/3 rounded-lg shadow-md sm:p-6 lg:p-8">
            <Tabs
                list={categories}
                activeTab={activeTab}
                onTabSwitch={onTabSwitch}
                />
        </div>
    )
}

export default Cart;
